class Config{adjustLayout(){var i=document.getElementsByTagName("html")[0];i.setAttribute("data-sidebar-view",window.innerWidth<=1024?"mobile":"default"),i.setAttribute("data-profile-view",window.innerWidth<=1024?"mobile":"default")}initWindowSize(){window.addEventListener("resize",()=>this.adjustLayout())}init(){this.adjustLayout(),this.initWindowSize()}}(new Config).init();